package cff.cashflowforecast.entity;import java.time.LocalDate;import java.util.UUID;import javax.persistence.*;import com.sun.istack.NotNull;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@Entity@AllArgsConstructor@NoArgsConstructorpublic class AccountDetail {	@Id	@GeneratedValue	@Column(name = "account_id")	private UUID accountId;	@NotNull	@Column(length = 12, unique = true, name = "account_number")	private long accountNumber;	@ManyToOne	@NotNull	@JoinColumn(name = "user_Id")	private CFUser user;	@NotNull	@Column(name = "created_date")	private LocalDate createdDate;	@NotNull	@Column(name = "created_by")	private String createdBy;	@NotNull	@Column(name = "updated_date")	private LocalDate updatedDate;	@NotNull	@Column(name = "updated_by")	private String updatedBy;	@NotNull	private String status;	@NotNull	@Column(name = "type_of_account")	private String typeOfAccount;	@NotNull	@Column(name = "type_of_proof")	private String typeOfProof;}