package cff.cashflowforecast.entity;import java.math.BigDecimal;import java.util.Date;import java.util.UUID;import javax.persistence.*;import com.sun.istack.NotNull;import lombok.AllArgsConstructor;import lombok.Data;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import lombok.ToString;@Data@Entity@AllArgsConstructor@NoArgsConstructorpublic class Transaction {	@Id	@GeneratedValue	@Column(name = "transcation_id")	private UUID transcationId;	@NotNull	private String currency;	@NotNull	private BigDecimal amount;	@NotNull	@Column(name = "transaction_type")	private String transcationType;	@NotNull	@Column(name = "transaction_date_and_time")	private Date transactionDateAndTime;	@NotNull	@Column(name = "created_by")	private String createdBy;	@NotNull	@Column(name = "updated_by")	private String updatedBy;	/*	 * one account have multiple Transcation	 */	@ManyToOne	@JoinColumn(name = "from_account")	private AccountDetail fromAccount;	@ManyToOne	@JoinColumn(name = "to_account")	private AccountDetail toAccount;}